import { Component, OnInit } from '@angular/core';
import { Todo } from "./../../models/Todo";

@Component({
  selector: 'app-todos',
  templateUrl: './todos.component.html',
  styleUrls: ['./todos.component.scss']
})   



export class TodosComponent implements OnInit {

  title = " title template litteral todos";

  todos:Todo[] = []; 

  constructor() { }

  ngOnInit(): void { 
    console.log("OnInit")
    this.todos = [

      {
        content: 'First todo',
        completed: false
      },

      {
        content: 'Second todo',
        completed: true
      }
    ]
  }

}
<p>todos works!</p>
<p>
  {{ title }}
</p>

<div class="AppUI__Todos">
  <div class="AppUI__Todos__id">0</div>
  <div class="AppUI__Todos__content">my todo</div>
  <div class="AppUI__Todos__delete">remove</div>
</div>
