import { Component, OnInit } from "@angular/core";
import { Todo } from "./../../models/Todo";
import { FormBuilder ,FormGroup, FormGroupDirective, Validators} from "@angular/forms";

@Component({
  selector: "app-todos",
  templateUrl: "./todos.component.html",
  styleUrls: ["./todos.component.scss"],
})
export class TodosComponent implements OnInit {
  title = " title template litteral todos";

  todos: Todo[] = [];
  inputTodo: string = "";
  formDirective!:FormGroupDirective
  registerForm!:FormGroup 
  submitted =false;

  constructor(private formBuilder:FormBuilder) { }

  ngOnInit(): void {
    // todo validation
    this.registerForm = this.formBuilder.group({
    firstName:['', Validators.required]

    },{  updateOn: 'submit' })

    console.log("OnInit");
    this.todos = [
      {
        content: "First todo",
        completed: false,
      },{
        content: "Second todo",
        completed: true,
      },
    ];
  }

  toggleDone(id: number) {
    this.todos.map((v, i) => {
      if (i == id) v.completed = !v.completed;

      return v;
    });
  }

  addTodo() {
    this.submitted = true;
    if(this.registerForm.invalid ){ 
      return;

    } 
    this.todos.push({
      content: this.inputTodo,
      completed: false,

    }); 

    if(this.registerForm.invalid ){ 

    }

     }



  deleteTodo(id: number) {
    this.todos = this.todos.filter((v, i) => i !== id);
  }

  isPurple = false;
  isGreen = false;

  updateColor() {
    this.isPurple = !this.isPurple;

  }

  updateColorGreen(){

    this.isGreen = !this.isGreen;
  }
}
<div class="AppUI__Todos">

<form #form="ngForm" [formGroup]="registerForm" #localForm="ngForm" (ngSubmit)="addTodo()">
  <label for="firstName">add a todo </label>


<input type="text"
name="inputTodo"
placeholder="Enter Todo...."
class="AppUI__Todos__input-todo"
[(ngModel)]="inputTodo" 
formControlName="firstName"
value="inputTodo"
 [ngClass]="{'AppUI__utility__is-invalid': submitted && registerForm.controls.firstName.errors}"
/>

 <div *ngIf="(registerForm.controls.firstName.dirty || form.submitted) && !registerForm.controls.firstName.valid" role="alert">

  Please enter username

  </div>


<input type="submit" value="add todo" class="AppUI__button--submit" />

</form>

  <section class="AppUI__todos " *ngFor="let todo of todos; let i = index" >
    <div class="AppUI__todos__todo" [class.AppUI__utility__purple] = "isPurple == false"   [class.AppUI__utility__green] = "isGreen == false"  *ngIf="!this.todo.completed"  >
    <div class="AppUI__todos__todo__id">{{ i }}</div>
    <div class="AppUI__todos__todo__content" (click)="toggleDone(i)" >{{ todo.content }}</div>
    <div class="AppUI__todos__todo__delete" (click)="deleteTodo(i)">remove</div>

    <button type="button" class="AppUI__button--toggle"  (click)="updateColor()">change border color to red</button>
    <button type="button" class="AppUI__button--toggle"  (click)="updateColorGreen()">change border color to green</button>
  </div>
  </section>


  <section>
<h5>completed list</h5>

<section class="AppUI__todos" *ngFor="let todo of todos; let i = index">
  <div  *ngIf="this.todo.completed"  class="AppUI__todos__todo {{ todo.completed ? 'done' : '' }}" >
  <div class="AppUI__todos__todo__id">{{ i }}</div>
  <div class="AppUI__todos__todo__content">{{ todo.content }}</div>
  <div class="AppUI__todos__todo__delete" (click)="deleteTodo(i)">remove</div>
</div>
</section>



  </section> 
</div>
